const readdir = promisify(fs.exists);
const unlinkdir = promisify(fs.unlink);
const writefiledir = promisify(fs.writeFile);
sharp.cache(false);

const writeImage = file => {
  return new Promise((resolve, reject) => {
    sharp(file.path).resize(150, 150).toBuffer((err, buffer) => {
      if (err) return reject(err);
      writefiledir(file.path, buffer);
      resolve();
    });
  });
};

let logoName = null;

const settingImage = async (logo, file) => {
  if (logo !== null) {
    try {
      const checkFile = await readdir(pathName + logo);

      if (checkFile) {
        if (file) {
          await unlinkdir(pathName + logo);
          await writeImage(file.path);
          logoName = file.filename;
        }
      }
    } catch (err) {
      res.json({
        message: err
      });
    }
  } else {
    if (file) {
      await writeImage(file.path);
      logoName = file.filename;
      console.log(file.filename);
    }
  }
};

const restaurant = {
  getRestaurantData: async (req, res) => {
    await models.Restaurant.findAll().then(rest => {
      if (!rest) return res.status(200).json({
        message: "Success",
        data: []
      });
      res.status(200).json({
        message: "Success",
        data: rest
      });
    }).catch(err => {
      res.status(500).json({
        message: err
      });
    });
  },
