"use strict";

const User = require("../model/Users");

const Sequelize = require("sequelize");

const Op = Sequelize.Op;

const bcrypt = require("bcryptjs");

const async = require("async");

const crypto = require("crypto");

const nodemailer = require("nodemailer");

const sharp = require("sharp"),
      fs = require("fs"),
      request = require("request");

const {
  promisify
} = require("util");

const path = require("path");

const helperUser = require("../helper/user");
/******************* provider image  ********************/


const dirName = path.dirname(require.main.filename);
const pathName = `${dirName}/public/images/users/`;
let avatarName = null;
const readdir = promisify(fs.exists);
const unlinkdir = promisify(fs.unlink);
const writefiledir = promisify(fs.writeFile);

const writeImage = path => {
  return new Promise((resolve, reject) => {
    sharp(path).resize(150, 150).toBuffer(async (err, buffer) => {
      if (err) return reject(err);
      await writefiledir(path, buffer);
      resolve();
    });
  });
};

const settingImage = async (logo, file) => {
  if (logo !== null) {
    try {
      const checkFile = await readdir(pathName + logo);

      if (checkFile) {
        if (file) {
          await unlinkdir(pathName + logo);
          await writeImage(file.path);
          avatarName = file.filename;
        } else {
          await unlinkdir(pathName + logo);
        }
      }
    } catch (err) {
      res.json({
        message: err
      });
    }
  } else {
    if (file) {
      await writeImage(file.path);
      avatarName = file.filename;
    }
  }
};
/************************* **************************/


const saveUser = async (req, res) => {
  console.log(req.body.password);
  await bcrypt.hash(req.body.password, 10, async (err, hash) => {
    if (err) {
      return res.status(500).json({
        error: err
      });
    } else {
      const user = {
        username: req.body.username,
        password: hash,
        name: req.body.name,
        lastname: req.body.lastname,
        email: req.body.email,
        telephone: req.body.telephone,
        address: req.body.address
      };
      await User.create(user).then(result => {
        const response = helperUser.loginJWT(result);
        res.status(200).json(response);
      }).catch(err => {
        res.status(500).json({
          error: err
        });
      });
    }
  });
};

const spreadNameFromFacebook = name => {
  return name.split(" ");
};

const downloadImageFromUrl = async (url, provider_id) => {
  let options = {
    url: url,
    method: "get",
    encoding: null
  };
  const imageName = provider_id;
  await request(options, (err, res, body) => {
    if (err) {
      imageName = "noimg";
    } else {
      fs.writeFileSync(pathName + provider_id + ".jpg", body);
    }
  });
  return imageName + ".jpg";
};

const users = {
  userAllData: async (req, res) => {
    await User.findAll().then(user => {
      res.status(200).json({
        message: "Success",
        data: user
      });
    }).catch(err => {
      res.status(500).json({
        message: err
      });
    });
  },
  userDataById: async (req, res) => {
    await User.findOne({
      where: {
        user_id: req.decoded.user_id
      }
    }).then(user => {
      res.status(200).json({
        user: user
      });
    }).catch(err => {
      res.status(500).json({
        message: err
      });
    });
  },
  signUpUsers: async (req, res) => {
    await saveUser(req, res);
  },
  checkUsername: async (req, res) => {
    await User.findOne({
      attributes: ["username"],
      where: {
        username: req.body.username
      }
    }).then(result => result !== null).then(isUnique => {
      res.status(200).json({
        username: isUnique
      });
    });
  },
  checkEmail: async (req, res) => {
    await User.findOne({
      attributes: ["email"],
      where: {
        email: req.body.email
      }
    }).then(result => result !== null).then(isUnique => {
      res.status(200).json({
        email: isUnique
      });
    });
  },
  loginUsers: async (req, res) => {
    const user = await User.findOne({
      attributes: ["user_id", "password", "email"],
      where: {
        username: req.body.username
      }
    });

    if (user) {
      const match = await bcrypt.compare(req.body.password, user.password);

      if (!match) {
        return res.status(401).json({
          message: "Please check username or password."
        });
      }

      const response = helperUser.loginJWT(user);
      return res.status(200).json({
        status: true,
        response
      });
    } else {
      res.status(200).json({
        message: "Please check username or password.",
        status: false
      });
    }
  },
  refreshToken: async (req, res) => {
    await User.findOne({
      attributes: ["user_id"],
      where: {
        user_id: req.session.userId
      }
    }).then(user => {
      if (!user) {
        res.status(401).json({
          message: "cannot refresh token"
        });
        return;
      }

      const response = helperUser.loginJWT(user);
      res.status(200).json(response);
    }).catch(err => {
      res.status(500).json({
        error: err
      });
    });
  },
  changePassword: (req, res) => {
    if (req.body.password == req.body.confirm_password) {
      User.findOne({
        attributes: ["user_id"],
        where: {
          user_id: req.decoded.user_id
        }
      }).then(user => {
        if (!user) return res.status(500).json({
          message: "user not found"
        });
        bcrypt.hash(req.body.password, 10, (err, hash) => {
          if (err) return res.json({
            message: err
