      res.status(200).json({
        message: "Success",
        data: rest
      });
    }).catch(err => {
      res.status(500).json({
        message: err
      });
    });
  },
  getRestaurantDataByName: async (req, res) => {
    await Restaurants.findOne({
      attributes: ["res_id"],
      where: {
        res_name: req.body.res_name,
        user_id: req.decoded.user_id
      }
    }).then(rest => {
      if (!rest) return res.status(200).json({
        message: "success",
        data: []
      });
      res.status(200).json({
        message: "Success",
        data: rest
      });
    }).catch(err => {
      res.status(500).json({
        message: err
      });
    });
  },
  getRestaurantDataById: async (req, res) => {
